from os import path
from sys import argv
import numpy as np

def file_path(file):
    return "%s/%s" % (path.dirname(argv[0]) if path.dirname(argv[0]) else ".", file)


def load(file):
    return open(file_path(file), "r").read().rstrip()

debug = False
def dprint(s):
    if debug:
        print(s)

def ex1(data):
    return sum(map(lambda x: 1 if x == '(' else -1, data))
    

def ex2(data):
    floor = 0
    for i, c in enumerate(data):
        floor += 1 if c == '(' else -1
        if floor < 0:
            return i + 1
    return None

assert ex1("(())") == 0
assert ex1("()()") == 0
assert ex1("))(((((") == 3
assert ex1("())") == -1
assert ex1("))(") == -1
assert ex1(")))") == -3
assert ex1(")())())") == -3

